import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import "./schedule.css";
import axios from "axios";

const Schedule = () => {
  const token = localStorage.getItem("token");
  const { id } = useParams();
  const [result, setResult] = useState([]);
  const [message, setMessage] = useState("");
  const [btata, setBtata] = useState("");
  const [time, setTime] = useState("");
  const [date, setDate] = useState("");
  const [sa, setSa] = useState(false);

  useEffect(() => {
    axios
      .get(`http://localhost:5000/doctor/${id}`)
      .then((result) => {
        setResult(result.data);
      })
      .catch((err) => {});
  }, []);

  const info = (req, res) => {
    axios
      .post(
        `http://localhost:5000/schedule/${id}`,
        {
          time,
          date,
        },
        {
          headers: {
            authorization: "Bearer " + token,
          },
        }
      )
      .then((result) => {
        console.log(result.data);
        setMessage("Booking successfully");
      })
      .catch((err) => {
        setMessage("you must choose date and time !!");
      });
  };

  return (
    <div>
      {result &&
        result.map((element, index) => {
          return (
            <div className="schedule-card">
              <div>
                <img src={element.img} />
              </div>
              <div>
                <h2>
                  {element.firstName} {element.lastName}
                </h2>
                <p> {element.price}</p>
                <p className=""> {element.description}</p>

                <label className="">Choose Date: </label>
                <input
                  className=""
                  type="date"
                  min="2021-07-13"
                  max="2021-08-30"
                  onChange={(e) => {
                    setDate(e.target.value);
                  }}
                />
                <label className="">Choose Time: </label>
                <input
                  className="time"
                  type="time"
                  name="time"
                  onChange={(e) => {
                    setTime(e.target.value);
                  }}
                />
                <Button variant="outline-secondary" onClick={info}></Button>

                <p className="book-done">{message}</p>
              </div>
            </div>
          );
        })}
    </div>
  );
};
export default Schedule;
